mapper.select:

select * from (
    select rownum as rn, a.* from (
        select b.*, coalesce(r.replycnt, 0) as replycnt
        from tbl_board b
            left join (
                select board_no, count(*) as replycnt
                from tbl_reply
                group by board_no
            ) r on b.board_no = r.board_no
        where board_category_cd = #{code}
        and title like '%'||#{keyword}||'%'
        order by b.board_no desc
    ) a
    order by rownum
)
where rn <= #{page} * #{boardPerPage}
and rn > (#{page}-1) * #{boardPerPage}

mapper.update:
update tbl_board set
       product_id = #{product_id}, title = #{title}, content = #{content}, upddate = sysdate,
       mainiamge = #{mainimage} where board_no = #{board_no}
