mapper.select:
select count(*)
from TBL_MEMBER
where EMAIL = #{email}

mapper.update:
update tbl_member
set password = #{password}
where email = #{email}

update tbl_member
set nickname = #{nickname}, password = #{password}, phonenumber = #{phonenumber}, address = #{address}
where email = #{email}

update tbl_member
set nickname = #{nickname}, phonenumber = #{phonenumber}, address = #{address}
where email = #{email}

mapper.select:

select *
from (
select rownum as rn, a.*
from (
select *
from tbl_member
where member_type_cd = 102
and (email like '%'||#{keyword}||'%'
or nickname like '%'||#{keyword}||'%')
order by member_no asc
) a
)
where rn <= #{page} * #{boardPerPage}
and rn > (#{page}-1) * #{boardPerPage}

mapper.update:

update tbl_member
set isactivated = #{isactivated}
where email = #{email}
