mapper.insert:
insert into TBL_ORDERPRODUCT(orderproduct_id, order_id, product_id, member_no, amount, price, order_status_cd) select seq_orderproduct_id.nextval, order_id, product_id, member_no, amount, product_price , order_status_cd from (
select #{order_id} as order_id, #{item.product_id} as product_id, #{item.member_no} as member_no, #{item.amount} as amount, #{item.product_price} as product_price, 301 as order_status_cd from dual
)
